import style from "./Card.module.css";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { addFavorite, removeFavorite } from "../../redux/actions";
import { useState } from "react";


function Card({ id, name, species, gender, image, onClose, addFavorite, removeFavorite }) {
   const [isFav, setIsFav] = useState(false);

   const handleFavorite = () => {
      if(isFav) {
         setIsFav(false);
         removeFavorite(id);
      } else {
         setIsFav(true);
         addFavorite({ id, name, species, gender, image, onClose, addFavorite, removeFavorite });
      }
   };


   return (      
      <div className={style.container}>
         {isFav ? (
            <button onClick={handleFavorite}>‚ù§Ô∏è</button>
         ) : (
            <button onClick={handleFavorite}>ü§ç</button>
         )}
         <div>
            <button className={style.buttonClose} onClick={() => onClose(id)}>X</button>
         </div>
         <Link to={`/detail/${id}`}>
            <h2 className={style.nameCard}>{name}</h2>         
         </Link>
            <img className={style.imageCard} src={image} alt={name} /> 
            <h2 className={style.specieCard}>Specie: {species}</h2>
            <h2 className={style.genderCard}>Gender: {gender}</h2>
      </div>
   );
}

const mapDispatchToProps = (dispatch) => {
   return{
      addFavorite: (character) => {
         dispatch(addFavorite(character));
      },
      removeFavorite: (id) => {
         dispatch(removeFavorite(id));
      },
   };
};

export default connect(null, mapDispatchToProps)(Card);
