import './App.css';
import Cards from './components/Cards/Cards';
import Nav from './components/Nav/Nav';
//import characters from './data.js';
import Video from "./components/assets/images/video-fondo.mp4";
import { useState } from 'react';
import { Route, Routes } from 'react-router-dom';



function App () {  
  const [characters, setCharacters] = useState([]);

  const onSearch = (id) => {
    const URL_BASE = "https://be-a-rym.up.railway.app/api";
    const KEY = "170c78cef19d.45d45c96ca62c65aab5a";   

    if(characters.find((char) => char.id === id)) {
      return alert ("Personaje repetido");
    }   

    fetch(`${URL_BASE}/character/${id}?key=${KEY}`)
    .then(response => response.json())
    .then(data => {
      if(data.name) {  //validacion para no agregar un personaje repetido        
        setCharacters((oldChars) => [...oldChars, data]);        
        
      } else {        
        alert("No hay personajes con ese ID");
      }
    });
  };

  const onClose = (id) => {
    setCharacters(characters.filter((char) => char.id !== id));
  };

  return (
    <div className='App' style={{ padding: '25px' }}>
      <video autoPlay loop muted><source src={Video} type='video/mp4'/></video>      
      <div>

        <Routes>
          <Route path="/home" />
        </Routes>
        <Nav onSearch={onSearch} />      
        <Cards characters={characters} onClose={onClose} />       
        </div>      
                               
                      
    </div>
  )
}

export default App;
