import { useSelector, useDispatch } from "react-redux";
import Card from "../Card/Card";
import { filterCarsd, orderCards } from "../../redux/actions";
import style from "./Favorites.module.css";
import axios from "axios";
import { useEffect } from "react";
export const GET_FAVORITES = "GET_FAVORITES";

const Favorites = () => {
    const favorites = useSelector((state) => state.myFavorites);
    const dispatch = useDispatch();

    const handlerOrder = (event) => {
        dispatch(orderCards(event.target.value))
    };

    const handlerFilter = (event) => {
        dispatch(filterCarsd(event.target.value))
    };
    
    //ojo
    useEffect(() => {
        dispatch(getFavorites());
    });

    //ojo para agregar favoritos
    const getFavorites = () => {
        return async function(dispatch) {
            const URL_BASE = "http://localhost:3001";
            const response = await axios.get(`${URL_BASE}/rickandmorty/fav`);
            dispatch({ type: GET_FAVORITES, payload: response.data });
        }
    };

    






    return(        
        <div>
            <div>
                <select className={style.selectUno} onChange={handlerOrder} >
                    <option value="order" disabled="disabled">Order by:</option>
                    <option value="Ascendente">Ascend</option>
                    <option value="Descendente">Descend</option>
                </select>
                <select className={style.selectDos} onChange={handlerFilter} >
                    <option value="filter" disabled="disabled">Filter by:</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Unknow">Unknow</option>
                    <option value="Genderless">Genderless</option>
                </select>
            </div>

            <div className={style.containerFavs}>
                {favorites.map(({ id, name, species, gender, image }) => {
                    return(
                        <Card 
                            key={id}
                            id={id}
                            name={name}
                            species={species}
                            gender={gender}
                            image={image}
                        />
                    );
                })}             



            </div>
        </div>
    );
};

export default Favorites;
